  <nav className={`${open ? menu.menuOpen : menu.menuClose }`}>
    <div>hj</div>
    <style jsx>{`
      nav {
        position: fixed;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background: #000;
        width: 100%;
        height: 100vh;
        transfrom: all .4
      }
    `}</style>
  </nav>
);

export default Menu;
