import Link from "next/link";
import menu from "../styles/menu.module.css";

const Menu = ({ open, setOpen }) => (
  <nav className={`${open ? menu.menuOpen : menu.menuClose}`}>
    <div className="wrapper">
      <div>

      </div>
    </div>

    <style jsx>{`
      nav {
        position: absolute;
        top: 20px;
        right: 10px;
        color: var(--primary-deep-colour);
        background: var(--primary-light-colour);
        width: 22em;
        height: 22em;
        transition: all 0.4s ease-in-out;
        z-index: 11;
      }

      .wrapper {
        position: absolute;
        top: 0;
        width: 100%;
        color: red;
        clip-path: polygon(9% 0, 46% 42%, 62% 100%, 0 100%, 0 0);
        -webkit-clip-path: polygon(9% 0, 46% 42%, 62% 100%, 0 100%, 0 0);
        height: 100%;
        background: #c6f6d5;
        z-index: 12;
      }

      a {
        color: var(--primary-deep-colour);
      }

      svg{
        width: 3em;
      }
    `}</style>
  </nav>
);

export default Menu;
