import { Fragment } from "react";
import Layout from "../component/Layout";
import utils from "../styles/utils.module.css";

const Project = () => {
  return (
    <Fragment>
      <Layout>
        <section>
          <div className={utils.container}>
            <h3>PROJECTS</h3>
            <div className="project-wrapper">
              <div className="project">
                <div className="project-img">
                  <img src="/images/Geo-Search.png" />{" "}
                </div>
                <div>
                  <p className="name">GeoSearch</p>
                </div>
                <div className="project-details">
                  <h3>
                    <a>
                      geosearch
                      <svg
                        className="w-6 h-6"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                        xmlns="http://www.w3.org/2000/svg">
                        <path
                          stroke-linecap="round"
                          stroke-linejoin="round"
                          stroke-width="2"
                          d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14"></path>
                      </svg>
                    </a>
                  </h3>
                  <p>
                    geosearch is weather app that provides real time information
                    about a particular location, weather information spanning
                    upto the next seven days of the location.
                  </p>
                  <div className="tools">
                    <div>Javascript</div>
                    <div>PWA</div>
                    <div>Babel</div>
                    <div>Webpack</div>
                    <div>Firebase</div>
                  </div>
                </div>
              </div>
              <div className="project">
                <div className="project-img">
                  <img src="/images/Portfolio.png" />
                </div>
                <div>
                  <p className="name">Portfolio</p>
                </div>
                <div className="project-details">
                  <h3>
                    <a>
                      portfolio
                      <svg
                        className="w-6 h-6"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                        xmlns="http://www.w3.org/2000/svg">
                        <path
                          stroke-linecap="round"
                          stroke-linejoin="round"
                          stroke-width="2"
                          d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14"></path>
                      </svg>
                    </a>
                  </h3>
                  <p>Yeah, this very website.</p>
                  <div className="tools">
                    <div>Nextjs</div>
                    <div>CSS</div>
                    <div>Netlify</div>
                  </div>
                </div>
              </div>
              <div className="project">
                <div className="project-img">
                  <img src="/images/greeen.png" />
                </div>
                <div>
                  <p className="name">Greeen</p>
                </div>
                <div className="project-details">
                  <h3>
                    <a>
                      greeen
                      <svg
                        className="w-6 h-6"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                        xmlns="http://www.w3.org/2000/svg">
                        <path
