import utils from "../styles/utils.module.css";

const Hero = () => {
  return (
    <section>
      <div className={utils.container}>
        <div className="article">
          <article>
            <h6 className={utils.fontSm}>Hi, I'm a</h6>
            <h1 className={utils.fontLg}>
              Frontend Developer<span>.</span>{" "}
            </h1>
            <p>I enjoy building products for the web.</p>
            <a>
              <div>

              </div>
             
            </a>
          </article>
        </div>

        {/* <div className={`${utils.fontSm} hero-para`}>
          <p>
            Highly skilled at building fast, high performant web application
          </p>
          <p>Passionate about creating products useful in the society.</p>
        </div> */}
      </div>
      <style jsx>{`
        section {
          min-height: calc(100vh - 10em);
        }

        section > div {
          display: flex;
          flex-direction: column;
        }
        .article {
          display: flex;
        }

        .article p {
          font-size: 4em;
          line-height: 0.9;
          width: 85%;
          margin-top: 20px;
        }

        article {
          margin-top: 1em;
        }

        h6 {
          font-weight: 800;
          padding-left: 0.1em;
          margin-bottom: 10px;
          color: #c6f6d5;
        }

        a {
          background: transparent;
          display: inline-block;
          border: 1px solid #f0fff4;
          padding: 0.5em 1em;
          text-transform: uppercase;
          color: #f0fff4;
          cursor: pointer;
          font-size: 14px;
        }
        svg {
          width: 5em;
          margin-left: 3px;
        }
        .button {
          display: flex;
          align-items: center;
        }
      `}</style>
    </section>
  );
};

export default Hero;
