import menu from "../styles/menu.module.css";

const NavButton = ({ open, setOpen }) => {
  return (
    <button className={`${open ? menu.rotateBtn : menu.parallelBtn}`} onClick={() => setOpen(!open)}>
      {console.log(open)}
      <style jsx>{`
        button {
          position: relative;
          width: 3em;
          height: 1em;
          color: var(--primary-light-colour);
          background: none;
          border: none;
        }

        button:before {
          content: "";
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 2px;
          background: var(--primary-light-colour);
        }

        button:after {
          content: "";
          position: absolute;
          top: 1em;
          right: 0;
          width: 75%;
          height: 2px;
          background: var(--primary-light-colour);
        }
      `}</style>
    </button>
  );
};

export default NavButton;
