import Link from "next/link";
import menu from "../styles/menu.module.css";

const Menu = ({ open, setOpen }) => (
  <nav className={`${open ? menu.menuOpen : menu.menuClose}`}>
    <div></div>
    <div>
      <ul>
        <li>
          <Link href="/work">
            <a>Work</a>
          </Link>
        </li>
      </ul>
    </div>
    <style jsx>{`
      nav {
        position: fixed;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        color: var(--primary-deep-colour);
        background: var(--primary-light-colour);
        width: 100%;
        height: 100vh;
        transition: all 0.4s ease-in-out;
      }

      nav div:first-child {
        position: absolute;
        top: 0;
        width: 100%;
        color: red;
        clip-path: polygon(9% 0, 46% 42%, 62% 100%, 0 100%, 0 0);
        height: 100%;
        background: #c6f6d5;
        z-index: -1;
      }

      a {
        color: var(--primary-deep-colour);
      }
    `}</style>
  </nav>
);

export default Menu;
