const Hero = () => {
  return (
    <section>
      <div className={`${utils.container} ${utils.wrapper}`}>
        <div className="article">
          <article>
            <h6>Peter Awotola</h6>
            <h1 className={utils.fontLg}>
              Frontend Developer<span>.</span>{" "}
            </h1>
            <p>I build products for the web.</p>
          </article>
          <figure>
            <img src="/images/propic.jpg" />
          </figure>
        </div>

        <div className="hero-para">
          <p>
            Highly skilled at building fast, high performant web application
          </p>
          <p>Specialized in creating products useful in the society.</p>
        </div>
      </div>
      <style jsx>{`

      `}</style>
    </section>
  );
};

