import utils from "../styles/utils.module.css";

const Hero = () => {
  return (
    <section>
      <div className={`${utils.container} ${utils.wrapper}`}>
        <div className="article">
          <article>
            <h6 className={utils.fontSm}>Hi, I'm a</h6>
            <h1 className={utils.fontLg}>
              Frontend Developer<span>.</span>{" "}
            </h1>
            <p>I enjoy building products for the web.</p>
          </article>
        </div>

        {/* <div className={`${utils.fontSm} hero-para`}>
          <p>
            Highly skilled at building fast, high performant web application
          </p>
          <p>Passionate about creating products useful in the society.</p>
        </div> */}
      </div>
      <style jsx>{`
        section {
          min-height: calc(100vh - 10em);
        }

        section > div {
          display: flex;
          flex-direction: column;
        }
        .article {
          display: flex;
        }

        .article p {
          font-size: 4em;
          line-height: 0.9;
          width: 85%;
          margin-top: 20px;
        }

        article {
          margin-top: 1em;
        }

        h6 {
          font-weight: 800;
          padding-left: 0.1em;
         margin-bottom: 10px;
          color: #C6F6D5;
        }
        figure {
          width: 40em;
          height: 30em;
        }

        img {
          width: 100%;
          object-fit: cover;
          height: 100%;
          display: inline-block;
          background-color: #fff;
        }

        .hero-para {
          display: flex;
          width: 60%;
          margin-top: 4.5em;
          font-size: 20px;
          color: #C6F6D5;
        }

        .hero-para p:first-child {
          padding-right: 20px;
        }
      `}</style>
    </section>
  );
};

export default Hero;
