import menu from "../styles/menu.module.css";

const Resume = ({ open, setOpen }) => {
  return (
    <section>
      <div className="wrapper">
        <div className="link">
          <a>
            <svg
              
              aria-hidden="true"
              focusable="false"
              data-prefix="fab"
              data-icon="linkedin-in"
              role="img"
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 448 512">
              <path
                fill="blue"
                d="M100.28 448H7.4V148.9h92.88zM53.79 108.1C24.09 108.1 0 83.5 0 53.8a53.79 53.79 0 0 1 107.58 0c0 29.7-24.1 54.3-53.79 54.3zM447.9 448h-92.68V302.4c0-34.7-.7-79.2-48.29-79.2-48.29 0-55.69 37.7-55.69 76.7V448h-92.78V148.9h89.08v40.8h1.3c12.4-23.5 42.69-48.3 87.88-48.3 94 0 111.28 61.9 111.28 142.3V448z"></path>
            </svg>
          </a>
          <a>download</a>
        </div>
        <div className="skills">
          <section className="contact">
            <a href="/">peter.netlify.app</a>
            <a>peterawotola@gmail.com</a>
            <div>Lagos, Nigeria</div>
          </section>
          <section className="tech">
            <h3>Core Technologies:</h3>
            <ul>
              <li>JavaScript</li>
              <li>Sass</li>
              <li>React</li>
              <li>Next.js</li>
              <li>NodeJs</li>
              <li>ExpressJs</li>
              <li>MongoDb</li>
              <li>CSS-in-JS</li>
              <li>Bootstrap</li>
            </ul>
          </section>

          <section className="others">
            <h3>Others</h3>
            <ul>
              <li>Responsive web design</li>
              <li>PWAs</li>
              <li>Ejs</li>
              <li>Heroku</li>
              <li>SEO</li>
              <li>CMS</li>
            </ul>
          </section>
        </div>
        <div className="about">
          <section className="bio">
            <h1>Awotola Peter</h1>
            <p>Frontend Developer and Technical Writer</p>
            <p>
              Software developer with robust problem solving skills and passion
              for building products with enhanced user experience and maximizing
              user interface. I’m always ready to adapt according to project
              specifications irrespective of stack.
            </p>
          </section>
          <section className="tech-sm">
            <h3>Core Technologies</h3>
            <p>
              JavaScript, Sass, React, Next.js, NodeJs, ExpressJs, MongoDb,
              CSS-in-JS, PWAs, SEO, Performance Optimization
            </p>
          </section>
          <section className="exp">
            <h3>Experience</h3>
            <div className="exp-wrapper">
              <div>
                <header>
                  <h4>Build for SDG - Andela and Facebook</h4>{" "}
                  <span>Aug 2020 - Present</span>
                </header>
                <p>
                  Contributing to open source project and building industry-led
                  projects that gives high value, high impact in real world
                  experience.
                </p>
              </div>
              <div>
                <header>
                  <h4>Google Africa Developer Scholarship</h4>{" "}
                  <span>May 2020 - Present</span>
                </header>
                <p>
                  One of 30000 developers supported by the Andela Learning
                  Community to upskill on important Google technologies.
                </p>
                <ul>
                  <li>
                    Top 2000 developer in the weekly leaderboard challenge
                  </li>
                  <li>
                    Aced Plurasight skill IQ with an IQ of 271 out of 300 in
                    "Building websites with JavaScript"
                  </li>
                  <li>
                    Hands on practice with standard core web technologies like
                    JavaScript
                  </li>
                </ul>
              </div>
              <div>
                <header>
                  <h4>
                    HNG Internship <span>- Frontend Developer</span>
                  </h4>{" "}
                  <span>May 2020 - July 2020</span>
                </header>
                <p>
                  I developed various frontend infrastructure, design and
                  functionality over the course of the internship.
                </p>
                <ul>
                  <li>
                    Worked closely with designers and the management team to
                    convert designs into pixel perfect mockups which was sold on
                    envato market.
                  </li>
                  <li>
                    Designed and implemented a cross-browser, IE 11 compatible
                    marketing site for the product.
                  </li>
                  <li>
                    Used various collaboration tools on a daily basis to
                    communicate and work with product managers, developers and
                    designers.
                  </li>
                </ul>
              </div>
              <div>
                <header>
                  <h4>DevCTrainingWithAndela</h4>{" "}
                  <span>Aug 2019 - Dec 2019</span>
                </header>
                <ul>
                  <li>
                    Crafted and Orchestrated successful development of a web
                    application with accesbility, responsive design, solid code
                    structure as a priority
                  </li>
                </ul>
              </div>
            </div>
          </section>
          <section className="edu">
            <h3>Education</h3>
            <p>Ekiti State University - B.Agric</p>
            <span>2013 - 2019</span>
          </section>
          <section className="project">
            <h3>Projects</h3>
            <p>
              Links to my works can be found on{" "}
              <a>peter.netlify.app/projects</a>
            </p>
          </section>
        </div>
      </div>
      <style jsx>{`
        .wrapper {
          position: relative;
          display: flex;
          max-width: 1200px;
          margin: 5em auto;
          padding: 5em 5em;
          background: #f0fff4;
          color: #474747;
        }

        .link{
            position: absolute;
            top: 0;
            right: 0;
            padding: 1.5em 2em;
        }

        .link svg{
            width: 1em;
            vertical-align: 15%;
        }

        .link a{
            display: inline-block;
            padding-right: 8px;
        }

        .skills {
          margin-right: 4em;
        }

        .skills section {
          margin-bottom: 2em;
        }

        .skills h3 {
          color: green;
          margin-bottom: 4px;
        }

        .skills .contact a {
          cursor: pointer;
          display: inline-block;
          border-bottom: 4px solid green;
          color: green;
        }

        .skills .contact a,
        .skills .contact div {
          margin-bottom: 5px;
          font-weight: normal;
        }

        .skills li {
          font-size: 15px;
          padding-bottom: 4px;
        }

        .skills li:before {
          content: "◼";
          font-size: 14px;
          padding-right: 5px;
          opacity: 0.8;
        }

        .about h3 {
          color: green;
          font-size: 24px;
          padding: 8px 0;
          padding-bottom: 10px;
        }

        .bio {
          border-bottom: 1px solid green;
        }

        .bio h1,
        .bio p {
          width: 80%;
        }

        .bio h1 {
          font-size: 58px;
          color: green;
        }

        .bio p {
          padding-bottom: 22px;
          line-height: 1.6;
        }

        .tech-sm {
          display: none;
        }

        .exp {
          padding: 22px 0;
          line-height: 1.6;
        }

        .exp-wrapper div {
          margin-bottom: 15px;
        }

        .exp header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding-bottom: 5px;
        }

        .exp header h4 {
          color: green;
        }

        .exp header span {
          font-size: 15px;
          opacity: 0.8;
        }

        .exp header h4:after {
          content: "";
          width: 20em;
          height: 1px;
          background: green;
          display: inline-block;
          margin: 4px 10px;
        }

        .exp-wrapper div li {
          line-height: 1.7;
          margin-left: 1em;
          text-indent: -1.1em;
        }

        .exp-wrapper div li:before {
          content: "◼";
          padding-right: 5px;
          opacity: 0.8;
          font-size: 14px;
        }

        .edu span {
          font-size: 14px;
          opacity: 0.8;
        }

        .project a {
          color: green;
          border-bottom: 1px solid;
          cursor: pointer;
        }
      `}</style>
    </section>
  );
};

export default Resume;
