import utils from "../styles/utils.module.css";

const Showcase = () => {
  return (
    <section>
      <div>
        <div className="wrapper">
          <article className="article">
            <h2>I build and work on</h2>
            <p>Open source projects, web applications.</p>
            <button>
              <div className="button">
                see my work
                <svg
                  width="77"
                  height="22"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor">
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M17 8l4 4m0 0l-4 4m4-4H3"
                  />
                </svg>
              </div>
            </button>
          </article>
          <article className="article">
            <h2>
              Sometimes,
              <br /> i write
            </h2>
            <p>About things i've learnt over my career.</p>
            <button>
              <div className="button">
                read my articles
                <svg
                  width="77"
                  height="22"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor">
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M17 8l4 4m0 0l-4 4m4-4H3"
                  />
                </svg>
              </div>
            </button>
          </article>
        </div>
      </div>
      <style jsx>{`
        section {
          min-height: calc(100vh-8em);
        }
        section > div {
          display: flex;
          justify-content: center;
          padding: 100px 0;
          width: 100%;
        }
        .wrapper {
          display: flex;
          background: #276749;
        }

        .wrapper article:first-child {
          border-right: 2px solid #22543d;
        }
        article {
          width: 50%;
          padding: 4em;
        }

        h2 {
          font-size: 2.5em;
          line-height: 0.9;
          padding-bottom: 16px;
        }

        p {
          text-transform: lowercase;
          padding-bottom: 16px;
        }
        button {
          background: transparent;
          outline: none;
          border: 1px solid #f0fff4;
          padding: .5em 1em;
          text-transform: uppercase;
          color: #f0fff4;
          cursor: pointer;
          z-index: 2;
        }
        svg{
          width: 5em;
          margin-left: 3px;
        }
        .button {
          display: flex;
          align-items: center;
        }
      `}</style>
    </section>
  );
};

export default Showcase;
