import utils from "../styles/utils.module.css";

const About = () => {
  return (
    <section>
      <div className={utils.container}>
        <div className={utils.fontSm}>
          <h2>About me</h2>
          <h2>
            Hi! I'm Peter Awotola, <br />a Nigeria based software developer{" "}
          </h2>
          <p>
            I specialize in building pixel-perfect, scalable products using
            current best practices in web development. My goal is to utilize
            available tools and techniques to build high perfomant products that
            improves user experiences.
          </p>
          <p>
            Sometimes, i{" "}
            <a target="_blank" rel="noopener noreferrer" href="">
              write
            </a>{" "}
            soft skill articles based on things I've learned over the course of
            my career.
          </p>
          <p>
            When i'm not coding, i spend most of my time surfing through twitter
            or taking some amateur pictures on my phone.
          </p>
          <p>Here are a few technologies i've been working with recently:</p>
          <ul>
            <li>JavaScript</li>
            <li>React</li>
            <li>HTML and (S)CSS</li>
            <li>Node.js</li>
            <li>Express</li>
            <li>Mongoose</li>
          </ul>
        </div>

        <figure>
          <img src="/images/propic.jpg" />
        </figure>
      </div>
      <style jsx>{`
        section {
          display: flex;
          align-items: center;
          background-color: #f0fff4;
          color: green;
          min-height: 100vh;
          position: sticky;
          top: 20px;
          z-index: 3;
          border-top: 5px solid #b2f5ea;
        }

        section > div {
          display: grid;
          grid-template-columns: 2fr 1fr;
          gap: 50px;
          padding-top: 30px;
          padding-bottom: 40px;
        }

        h2 {
          margin-bottom: 15px;
        }

        h2:not(:first-child):after {
          content: "";
          display: inline-block;
          height: 4px;
          width: 30px;
          background: green;
        }

        p {
          margin-bottom: 10px;
        }

        ul {
          display: grid;
          grid-template-columns: 1fr 1fr;
          font-size: 18px;
        }

        li:before {
          content: "â–¶";
          font-size: 16px;
          margin-right: 5px;
        }
      `}</style>
    </section>
  );
};

export default About;
