import Link from "next/link";

const NavButton = () => {
    const [bool, setBool] = useState(false);

    const toggleBool = () => setBool(!bool);
  return (
    <button onClick={toggleBool}>
      {console.log(bool)}
      <style jsx>{`
        button {
          position: relative;
          width: 3em;
          height: 1em;
          color: var(--primary-light-colour);
          background: none;
          border: none;
        }

        button:before {
          content: "";
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 2px;
          background: var(--primary-light-colour);
        }

        button:after {
          content: "";
          position: absolute;
          top: 1em;
          right: 0;
          width: 75%;
          height: 2px;
          background: var(--primary-light-colour);
        }
      `}</style>
    </button>
  );
};

export default NavButton;
