import utils from "../styles/utils.module.css";

const About = () => {
  return (
    <section>
      <div className={utils.container}>
        <div className={utils.fontSm}>
          <h2>About me</h2>
          <p>Hello! I'm Peter, a Nigeria based software developer </p>
          <p>
            I specialize in building pixel-perfect, scalable products using
            current best practices in web development. My goal is to utilize
            available tools and techniques to build high perfomant products that
            improves user experiences.
          </p>
          <p>
            Sometimes, i{" "}
            <a target="_blank" rel="noopener noreferrer" href="">
              write
            </a>{" "}
            soft skill articles based on things I've learned over the course of
            my career.
          </p>
          <p>Here are a few technologies i've been working with recently:</p>
          <ul>
            <li>JavaScript</li>
            <li>React</li>
            <li>HTML and (S)CSS</li>
            <li>Node.js</li>
            <li>Express</li>
            <li>Mongoose</li>
          </ul>
        </div>

        <figure>
          <img src="/images/propic.jpg" />
        </figure>
      </div>
      <style jsx>{`
        section {
          background-color: #f0fff4;
          color: green;
          min-height: 100vh;
        }

        section > div {
          display: grid;
          grid-template-columns: 2fr 1fr;
          padding-top: 60px;
          padding-bottom: 60px;
        }

        ul{
          display: grid;
        }
      `}</style>
    </section>
  );
};

export default About;
