import utils from "../styles/utils.module.css";

const Hero = () => {
  return (
    <section>
      <div className={`${utils.container} ${utils.wrapper}`}>
        <div className="article">
          <article>
            <h1 className={utils.fontLg}>
              Frontend Developer<span>.</span>{" "}
            </h1>
            <p>I enjoy building products for the web.</p>
          </article>
          <figure>
            <img src="/images/propic.jpg" />
          </figure>
        </div>

        <div className="hero-para">
          <p>
            Highly skilled at building fast, high performant web application
          </p>
          <p>Specialized in creating products useful in the society.</p>
        </div>
      </div>
      <style jsx>{`
        section {
          min-height: calc(80vh - 10em);
        }

        section > div{
          display: flex;
          flex-direction: column;
        }
        .article {
          display: flex;
        }

        .article p{
           font-size: 4.5em;
           line-height: 0.9;
           margin-top: 12px;
        }

        article{
          margin: auto;
        }

        h6{
          font-size: 15px;
          font-weight: 800;
          padding-left: 0.1em;
        }
        figure {
          width: 40em;
          height: 26em;
        }

        img {
          width: 100%;
          object-fit: cover;
          height: 100%;
          display: inline-block;
          background-color: #fff;
        }

        .hero-para{
          display: flex;
          width: 50%;
        }

        .hero-para p{
          font-size: 16px
          padding-right: 10px;
        }

      `}</style>
    </section>
  );
};

export default Hero;
