import { useEffect, useRef } from "react";
import Head from "next/head";
import Layout from "../component/Layout";
import Hero from "../component/Hero";
import About from "../component/About";
import Showcase from "../component/Showcase";
import Contact from "../component/Contact";
import utils from "../styles/utils.module.css";

const Home = () => {
  const characterRef = useRef(null);

  const observerOptions = {
    root: null,
    rootMargin: "0px",
    threshold: 0,
    delay: 100,
  };


  useEffect(() => {
    const observer = new IntersectionObserver(entries => {
      console.log(entries)
    }, observerOptions)

    observer.observe(characterRef)
  })
  return (
    <Layout>
      <div>
        <Head>
          <title>Peter Awotola | Frontend Developer</title>
          <meta
            name="description"
            content="Portfolio website for Peter Awotola"
          />
          <link
            href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.css"
            rel="stylesheet"
          />
          <link
            href="https://fonts.googleapis.com/css2?family=Nunito+Sans:wght@300;400;600;700;800&display=swap"
            rel="stylesheet"
          />
        </Head>
        <Hero />
        <About />
        <Showcase />
        <Contact />
      </div>
    </Layout>
  );
}
  
export default Home;
