import utils from "../styles/utils.module.css";

const Hero = () => {
  return (
    <section>
      <div className={`${utils.container} ${utils.wrapper}`}>
        <div className="article">
          <article>
            <h6>Peter Awotola</h6>
            <h1 className={utils.fontLg}>
              Frontend Developer<span>.</span>{" "}
            </h1>
            <p>I build products for the web.</p>
          </article>
          <figure>
            <img src="/images/propic.jpg" />
          </figure>
        </div>

        <div className="hero-para">
          <p>
            Highly skilled at building fast, high performant web application
          </p>
          <p>Specialized in creating products useful in the society.</p>
        </div>
      </div>
      <style jsx>{`
        section {
          display: flex;
          justify-content: center;
          align-items: flex-start;
          min-height: 100vh;
        }
        .article {
          display: flex;
          flex-direction: column;
        }

        .article p{
           font-size: 2.5em;
           line-height: 0.9;
           margin-top: 12px;
        }

        h6{
          font-size: 15px;
          font-weight: 800;
          padding-left: 0.1em;
        }
        figure {
          width: 20em;
          height: 22em;
          display: none;
        }

        img {
          width: 100%;
          object-fit: cover;
          height: 100%;
          display: inline-block;
          background-color: #fff;
        }

        .hero-para{
          display: flex;
          order: 0;
        }

        .hero-para p{
          padding-right: 10px;
        }

        @media screen and (max-width: 580px){

        }
      `}</style>
    </section>
  );
};

export default Hero;
