import Link from "next/link";
import menu from "../styles/menu.module.css";

const Menu = ({ open, setOpen }) => (
  <nav className={`${open ? menu.menuOpen : menu.menuClose}`}>
    <div className="bg"></div>
    <div className="wrapper">
      <div className="navItem">
        <div className="navLinks">
          <ul>
            <li>
              <Link href="/work">
                <a>Project</a>
              </Link>
            </li>
            <li>
              <Link href="/resume">
                <a>Resume</a>
              </Link>
            </li>
          </ul>
        </div>
        <div className="contact">
          <h3>SAY HELLO</h3>
          <ul>
            <li>
              <a>peterawotola@gmail.com</a>
            </li>
            <li>
              <a>08101211029</a>
            </li>
          </ul>
        </div>
        
      </div>
    </div>

    <style jsx>{`
      nav {
        position: absolute;
        top: 0px;
        right: 0px;
        color: var(--primary-deep-colour);
        background: var(--primary-light-colour);
        width: 100%;
        height: 100vh;
        transition: all 0.4s ease-in-out;
        z-index: 11;
      }

      .bg {
        position: absolute;
        top: 0;
        width: 100%;
        color: #22543d;
        clip-path: polygon(9% 0, 46% 42%, 62% 100%, 0 100%, 0 0);
        -webkit-clip-path: polygon(9% 0, 46% 42%, 62% 100%, 0 100%, 0 0);
        height: 100%;
        background: #c6f6d5;
        z-index: 12;
      }

      .navItem {
        display: flex;
        flex-direction: column;
        text-align: right;
        padding: 2em;
        padding-right: 2.5em;
        position: absolute;
        right: 0;
        top: 15%;
        z-index: 34;
      }

      a {
        color: var(--primary-deep-colour);
      }

      svg {
        width: 3em;
      }

      .navLinks {
        margin-bottom: 1.5em;
      }
      .navLinks a {
        font-size: 46px;
        font-weight: 700;
      }

      .social {
        position: absolute;
        bottom: 0;
      }
    `}</style>
  </nav>
);

export default Menu;
