import utils from "../styles/utils.module.css";

const About = () => {
  return (
    <section>
      <div className={utils.container}>
        <h2>About me</h2>
        <div>

        </div>
        
      </div>
      <style jsx>{`
        section {
          display: flex;
          align-items: center;
          background-color: #f0fff4;
          color: green;
          min-height: 100vh;
          position: sticky;
          top: 0px;
          z-index: 3;
          border-top: 8px solid #b2f5ea;
        }

        section > div {
          display: grid;
          grid-template-columns: 2fr 1fr;
          gap: 50px;
          padding-top: 30px;
          padding-bottom: 40px;
        }

        h2 {
          margin-bottom: 15px;
        }

        h2:not(:first-child):after {
          content: "";
          display: inline-block;
          height: 4px;
          width: 30px;
          background: green;
        }

        p {
          margin-bottom: 10px;
        }

        ul {
          display: grid;
          grid-template-columns: 1fr 1fr;
          font-size: 18px;
        }

        li:before {
          content: "â–¶";
          font-size: 16px;
          margin-right: 5px;
        }
      `}</style>
    </section>
  );
};

export default About;
