import utils from "../styles/utils.module.css";

const Hero = () => {
  return (
    <section>
      <div className={utils.container}>
        <div className="article">
          <article>
            <h6 className={utils.fontSm}>Hi, I'm a</h6>
            <h1 className={utils.fontLg}>
              Frontend Developer<span>.</span>{" "}
            </h1>
            <p>I enjoy building products for the web.</p>
            <a>
              View Resume{" "}
              <svg
                width="77"
                height="22"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor">
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M17 8l4 4m0 0l-4 4m4-4H3"
                />
              </svg>
            </a>
          </article>
        </div>

        {/* <div className={`${utils.fontSm} hero-para`}>
          <p>
            Highly skilled at building fast, high performant web application
          </p>
          <p>Passionate about creating products useful in the society.</p>
        </div> */}
      </div>
      <style jsx>{`
        section {
          min-height: calc(100vh - 10em);
        }

        section > div {
          display: flex;
          flex-direction: column;
        }
        .article {
          display: flex;
        }

        .article p {
          font-size: 4em;
          line-height: 0.9;
          width: 85%;
          margin-top: 20px;
        }

        article {
          margin-top: 1em;
        }

        h6 {
          font-weight: 800;
          padding-left: 0.1em;
          margin-bottom: 10px;
          color: #c6f6d5;
        }

        svg {
          width: 5em;
          margin-left: 3px;
        }
      `}</style>
    </section>
  );
};

export default Hero;
