import utils from "../styles/utils.module.css";

const Hero = () => {
  return (
    <section>
      <div className={`${utils.container} ${utils.wrapper}`}>
        <div className="article">
          <article>
            <h6>Peter Awotola</h6>
            <h1 className="font-xlg">
              Frontend Developer<span>.</span>{" "}
            </h1>
            <p>I love to build products for the web.</p>
          </article>
          <figure>
            <img src="/images/propic.jpg" />
          </figure>
        </div>

        <div className="hero-para">
          <p>
            Highly skilled at building fast, high performant web application
          </p>
          <p>Specialized in creating products useful in the society.</p>
        </div>
      </div>
      <style jsx>{`
        section {
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }
        .article {
          display: flex;
          justify-content: space-between;
          align-items: center;
        }

        h6{
          font-size: 18px;
          font-weight: 800;
        }
        figure {
          width: 20em;
          height: 22em;
          border: 1px solid #2c7a7b;
        }

        img {
          width: 100%;
          object-fit: cover;
          height: 100%;
          display: inline-block;
          margin-top: -2em;
          margin-left: -2em;
        }

        .hero-para{
          display
        }
      `}</style>
    </section>
  );
};

export default Hero;
