import utils from "../styles/utils.module.css";

const Contact = () => {
  return (
    <section>
      <div className={utils.container}>
        <div className="wrapper">
          <article>
            <h2>Send me a message!</h2>
            <p>
              I'm currently open to opportunities, I would love to learn more
              about what you're building.
            </p>
          </article>
          <form>
            <div className="nameemail">
              <div>
                <label>Your Name</label>
                <input placeholder="name" />
              </div>
              <div>
                <label>Email Address</label>
                <input placeholder="email" />
              </div>
            </div>
            <div>
              <textarea></textarea>
            </div>
            <button>
              <div>SEND</div>
              <svg
                width="77"
                height="22"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor">
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M17 8l4 4m0 0l-4 4m4-4H3"
                />
              </svg>
            </button>
          </form>
        </div>
      </div>

      <style jsx>
        {`
          section {
            background: #f0fff4;
            color: #22543d;
            min-height: 100vh;
            text-align: center;
            padding: 30px;
          }
          .wrapper {
            margin-top: 40px;
          }

          article {
            margin-bottom: 40px;
          }
          h2 {
            font-size: 2.5em;
            padding-bottom: 8px;
          }
          form {
            width: 60%;
            max-width: 500px;
            margin: auto;
          }

          input,
          textarea {
            background: transparent;
            border: none;
            border-bottom: 1px solid #22543d;
            color: #22543d;
            width: 100%;
          }

          .nameemail {
            display: flex;
            margin-bottom: 4em;
            justify-content: space-between;
            width: 100%;
          }

          .nameemail > div{
            width: 47%;
          }

          label{
            text-align: left;
          }

          input{
            padding: 1em;
          }

          textarea {
            width: 100%;
          }
        `}
      </style>
    </section>
  );
};

export default Contact;
